","# (фото, имя питомца, его возраст).\r","\r","import pytest\r","from selenium.webdriver.common.by import By\r","from settings import valid_email, valid_password\r","\r","\r","def test_pet_frends():\r","    \"\"\"Проверка карточек питомцев всех пользователей\r","    на наличие фото, имени и описания (порода и возраст)\"\"\"\r","\r","    # Установка неявного ожидания\r","    pytest.driver.implicitly_wait(10)\r","\r","    # Ввод эл.почты\r","    pytest.driver.find_element(By.ID, 'email').send_keys(valid_email)\r","\r","    # Ввод пароля\r","    pytest.driver.find_element(By.ID, 'pass').send_keys(valid_password)\r","\r","    # Клик по кнопке \"Войти\"\r","    pytest.driver.find_element(By.CSS_SELECTOR, 'button[type=\"submit\"]').click()\r","\r","    # Проверка того, что осуществлен переход на главную страницу пользователя\r","    assert pytest.driver.current_url == 'https://petfriends.skillfactory.ru/all_pets'\r","\r","    images = pytest.driver.find_elements(By.CSS_SELECTOR, '.card-deck .card-img-top')\r","    names = pytest.driver.find_elements(By.CSS_SELECTOR, '.card-deck .card-title')\r","    descriptions = pytest.driver.find_elements(By.CSS_SELECTOR, '.card-deck .card-text')\r","\r","    assert names[0].text != ''\r","\r","    for i in range(len(names)):\r","        assert images[i].get_attribute('src') != ''\r","        assert names[i].text != ''\r","        assert descriptions[i].text != ''\r","        assert ',' in descriptions[i].text\r","        parts = descriptions[i].text.split(\", \")\r","        assert len(parts[0]) > 0\r","        assert len(parts[1]) > 0"],"stylingDirectives":[[{"start":0,"end":97,"cssClass":"pl-c"}],[{"start":0,"end":36,"cssClass":"pl-c"}],[],[{"start":0,"end":6,"cssClass":"pl-k"},{"start":7,"end":13,"cssClass":"pl-s1"}],[{"start":0,"end":4,"cssClass":"pl-k"},{"start":5,"end":13,"cssClass":"pl-s1"},{"start":14,"end":23,"cssClass":"pl-s1"},{"start":24,"end":30,"cssClass":"pl-s1"},{"start":31,"end":33,"cssClass":"pl-s1"},{"start":34,"en